mapper.select:
select p.id, p.name, p.age, p.sex_code, p.post_id, (select sum(djs.score) from dj_score djs where djs.user_id = p.id and djs.add_year = #{year} ) as totalscore from dj_partymember p left join dj_user pu on p.id = pu.id
where p.dept_id = #{deptId}
order by p.party_post_code

select p.name, p.sex_code, p.education_code, p.party_post_code, p.birth_date, (select sum(table1.score) from dj_score table1 where table1.user_id = p.id and add_year = (select year(now()) )) as totalscore from dj_partymember p left join dj_user pu on p.id = pu.id where 1 = 1
and p.dept_id = #{deptId}
order by p.party_post_code

select p.id,p.name text from dj_partymember p
left join dj_dept d on d.id = p.dept_id
where d.dj_section_id = #{sectionId}
